<script setup>
import { ref } from 'vue';
import { useUserStore } from '../stores/userStore';
import { useRouter } from 'vue-router';
import { useNotificationStore } from '../stores/NotiStore';
const userStore = useUserStore();
const router = useRouter();
const open = ref(false);
const email = ref('');
const password = ref('');
const error = ref('');
const notiStore = useNotificationStore();
function login() {
    error.value = '';

    if (email.value && password.value) {
        const formData = new FormData();

        formData.append('email', email.value);
        formData.append('password', password.value);
        // eslint-disable-next-line no-unused-vars
        userStore.login(formData).then(res => {
            open.value = false;
            router.push({
                path : '/adminDashboard'
            });
        }).catch(error => {
            console.log('error', error);
        })

    } else {
        error.value = 'Require email & password!';
        notiStore.showNotification(error.value, 'error');
    }

}

function routeToAdminDash() {
  router.push({
    name : 'adminManageShows'
  })
}
</script>

